	&lt;span class=&#34;function&#34;&gt;Person &lt;span class=&#34;title&#34;&gt;p4&lt;/span&gt;&lt;span class=&#34;params&#34;&gt;(&lt;span class=&#34;string&#34;&gt;&amp;quot;赵云&amp;quot;&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;21&lt;/span&gt;)&lt;/span&gt;&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	s.&lt;span class=&#34;built_in&#34;&gt;insert&lt;/span&gt;(p1);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	s.&lt;span class=&#34;built_in&#34;&gt;insert&lt;/span&gt;(p2);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	s.&lt;span class=&#34;built_in&#34;&gt;insert&lt;/span&gt;(p3);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	s.&lt;span class=&#34;built_in&#34;&gt;insert&lt;/span&gt;(p4);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	&lt;span class=&#34;keyword&#34;&gt;for&lt;/span&gt; (set&amp;lt;Person, comparePerson&amp;gt;::iterator it = s.&lt;span class=&#34;built_in&#34;&gt;begin&lt;/span&gt;(); it != s.&lt;span class=&#34;built_in&#34;&gt;end&lt;/span&gt;(); it++)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		cout &amp;lt;&amp;lt; &lt;span class=&#34;string&#34;&gt;&amp;quot;姓名： &amp;quot;&lt;/span&gt; &amp;lt;&amp;lt; it-&amp;gt;m_Name &amp;lt;&amp;lt; &lt;span class=&#34;string&#34;&gt;&amp;quot; 年龄： &amp;quot;&lt;/span&gt; &amp;lt;&amp;lt; it-&amp;gt;m_Age &amp;lt;&amp;lt; endl;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;function&#34;&gt;&lt;span class=&#34;type&#34;&gt;int&lt;/span&gt; &lt;span class=&#34;title&#34;&gt;main&lt;/span&gt;&lt;span class=&#34;params&#34;&gt;()&lt;/span&gt; &lt;/span&gt;&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;